#! /bin/bash
export HOME=/home/ec2-user
cd $HOME
sudo dnf update -y
sudo dnf install -y podman git make wget cargo openssl openssl-devel net-tools skopeo 

# install oc-mirror
wget https://mirror.openshift.com/pub/openshift-v4/amd64/clients/ocp/4.16.6/oc-mirror.rhel9.tar.gz
tar xvf oc-mirror.rhel9.tar.gz
chmod +x oc-mirror && sudo mv oc-mirror /usr/local/bin/oc-mirror

# install go
wget https://go.dev/dl/go1.22.4.linux-amd64.tar.gz
sudo rm -rf /usr/local/go && sudo tar -C /usr/local -xzf go1.22.4.linux-amd64.tar.gz
export PATH=$PATH:/usr/local/go/bin

# Generate TLS cert for entreprise and enclave registries
openssl genrsa -out server.key 2048
openssl genrsa -out ca.key 2048

openssl ecparam -genkey -name secp384r1 -out server.key
openssl req -new -x509 -days 365 -key ca.key -subj "/C=CN/ST=GD/L=SZ/O=Acme, Inc./CN=Acme Root CA" -out ca.crt

openssl req -newkey rsa:2048 -nodes -keyout server.key -subj "/C=CN/ST=GD/L=SZ/O=Acme, Inc./CN=*.${HOSTNAME}" -out server.csr
openssl x509 -req -extfile <(printf "subjectAltName=DNS:${HOSTNAME},DNS:${HOSTNAME}") -days 365 -in server.csr -CA ca.crt -CAkey ca.key -CAcreateserial -out server.crt
openssl x509 -req -extfile <(printf "subjectAltName=DNS:${HOSTNAME},DNS:${HOSTNAME}") -days 365 -in server.csr -CA ca.crt -CAkey ca.key -CAcreateserial -out server.crt
sudo cp ca.crt /etc/pki/ca-trust/source/anchors/

