# Prepare registries
git clone https://github.com/distribution/distribution
cd distribution

# run entreprise registry
cat > cmd/registry/config-entreprise.yml << EOF
version: 0.1
log:
  level: info
  fields:
    service: registry
storage:
  delete:
      enabled: true
  cache:
    blobdescriptor: inmemory
  filesystem:
    rootdirectory: var/lib/registry
http:
  addr: :5000
  headers:
    X-Content-Type-Options: [nosniff]
  tls:
    certificate: /home/ec2-user/server.crt
    key: /home/ec2-user/server.key
EOF
go run cmd/registry/main.go serve cmd/registry/config-entreprise.yml & 

# run enclave registry
cat > cmd/registry/config-enclave.yml << EOF
version: 0.1
log:
  level: info
  fields:
    service: registry
storage:
  delete:
      enabled: true
  cache:
    blobdescriptor: inmemory
  filesystem:
    rootdirectory: var/lib/registry
http:
  addr: :7000
  headers:
    X-Content-Type-Options: [nosniff]
  tls:
    certificate: /home/ec2-user/server.crt
    key: /home/ec2-user/server.key
EOF
go run cmd/registry/main.go serve cmd/registry/config-enclave.yml & 

